param(    [Parameter(Mandatory = $true)]    [System.String]    $ComputerName)Configuration SCVMMStaticIPPoolTest{    Import-DscResource -ModuleName xSCVMM    node $ComputerName    {        # Static IP Pool configuration examples                xSCVMMIPPool Test_Remove        {            Ensure                          = 'Absent'            Name                            = 'Test-Remove'            SourceNetworkName               = 'Management'            SourceNetworkType               = 'LogicalNetwork'        }                xSCVMMIPPool Management_LN_Pool        {            Ensure                          = 'Present'            Name                            = 'Management_LN_Pool'            Description                     = 'Management Logical Network IP Pool'            SourceNetworkName               = 'Management'            SourceNetworkType               = 'LogicalNetwork'            SourceNetworkSite               = 'Management_0'            SourceNetworkSubnet             = '10.10.12.0/24'            IPAddressRange                  = '10.10.12.1-10.10.12.254'            Gateway                         = '10.10.12.1'            DnsServer                       = @('10.10.2.14','10.10.2.15')            DnsSuffix                       = 'lajolla.local'            DnsSearchSuffix                 = @('contoso.com','fabrikam.com')            WinsServer                      = @('10.10.2.14','10.10.2.15')            NetBIOSOverTCPIP                = $true            VIPReservation                  = '10.10.12.220-10.10.12.239'            OtherReservation                = '10.10.12.240-10.10.12.254'        }                xSCVMMIPPool Management_VM_Pool        {            Ensure                          = 'Present'            Name                            = 'Management_VM_Pool'            Description                     = 'Management VM Network IP Pool'            SourceNetworkName               = 'Management'            SourceNetworkType               = 'VMNetwork'            SourceNetworkSubnet             = '10.10.12.0/24'            DnsServer                       = @('10.10.2.14','10.10.2.15')            DnsSuffix                       = 'lajolla.local'            DnsSearchSuffix                 = @('contoso.com','fabrikam.com')            VIPReservation                  = '10.10.12.220-10.10.12.239'            OtherReservation                = '10.10.12.240-10.10.12.254'        }                xSCVMMIPPool PVLAN_LN_Pool        {            Ensure                          = 'Present'            Name                            = 'PVLAN-Based_LN_Pool'            SourceNetworkName               = 'PVLAN-Based-Test'            SourceNetworkType               = 'LogicalNetwork'            SourceNetworkSite               = 'PVLAN-Based-Test_0'        }                xSCVMMIPPool VLAN_LN_Pool        {            Ensure                          = 'Present'            Name                            = 'VLAN-Based_LN_Pool'            SourceNetworkName               = 'VLAN-Based-Test'            SourceNetworkType               = 'LogicalNetwork'            SourceNetworkSite               = 'VLAN-Based-Test_0'            SourceNetworkSubnet             = '10.88.0.0/24'            IPAddressRange                  = '10.88.0.1-10.88.0.100'            Gateway                         = '10.88.0.1;500'        }            }} $ConfigurationData = ` @{    AllNodes = `    @(        @{            NodeName = "*"            PSDscAllowPlainTextPassword = $true        }        @{            NodeName = $ComputerName        }    )}$MOFpath = "$env:SystemDrive\Test\MOF"SCVMMStaticIPPoolTest -OutputPath $MOFpath -ConfigurationData $ConfigurationDataStart-DscConfiguration -ComputerName $ComputerName -Wait -Force -Verbose -Debug -Path $MOFpath