param(    [Parameter(Mandatory = $true)]    [System.String]    $ComputerName)Configuration SCVMMLogicalSwitchTest{    Import-DscResource -ModuleName xSCVMM    node $ComputerName    {        # Logical Switch configuration examples                xSCVMMLogicalSwitch Test_Remove        {            Ensure                          = 'Absent'            Name                            = 'Test-Remove'        }                xSCVMMLogicalSwitch Test_Switch_1        {            Ensure                          = 'Present'            Name                            = 'Test_Switch_1'            Description                     = 'Test Logical Switch 1'            EnableSRIOV                     = $false            SwitchExtensions                = @("Microsoft Windows Filtering Platform")            UplinkMode                      = "Team"            UplinkPortProfiles              = @("Test_UPP1","Test_UPP2")            VirtualPorts                    = @("MediumPort;Medium bandwidth;Medium Bandwidth Adapter","HighPort;High bandwidth;High Bandwidth Adapter")        }                xSCVMMLogicalSwitch Test_Switch_2        {            # NOTE: SR-IOV must use the 'NoTeam' UplinkMode and is not compatible with the 'Microsoft Windows Filtering Platform' Switch Extension            Ensure                          = 'Present'            Name                            = 'Test_Switch_2'            Description                     = 'Test Logical Switch 2'            EnableSRIOV                     = $true            SwitchExtensions                = @()            UplinkMode                      = "NoTeam"            UplinkPortProfiles              = @("Test_UPP1")            VirtualPorts                    = @("SRIOVPort;SR-IOV;SR-IOV Profile")        }                xSCVMMLogicalSwitch Test_Switch_3        {            Ensure                          = 'Present'            Name                            = 'Test_Switch_3'        }            }} $ConfigurationData = ` @{    AllNodes = `    @(        @{            NodeName = "*"            PSDscAllowPlainTextPassword = $true        }        @{            NodeName = $ComputerName        }    )}$MOFpath = "$env:SystemDrive\Test\MOF"SCVMMLogicalSwitchTest -OutputPath $MOFpath -ConfigurationData $ConfigurationDataStart-DscConfiguration -ComputerName $ComputerName -Wait -Force -Verbose -Debug -Path $MOFpath