param(    [Parameter(Mandatory = $true)]    [System.String]    $ComputerName)Configuration SCVMMNetworkSettingTest{    Import-DscResource -ModuleName xSCVMM    node $ComputerName    {        # VMM Network Setting configuration examples                xSCVMMNetworkSetting Disable_AutoCreation        {            AutomaticLogicalNetworkCreation = $false            LogicalNetworkMatch             = "FirstDNSSuffixLabel"            BackupLogicalNetworkMatch       = "VirtualNetworkSwitchName"        }            }} $ConfigurationData = ` @{    AllNodes = `    @(        @{            NodeName = "*"            PSDscAllowPlainTextPassword = $true        }        @{            NodeName = $ComputerName        }    )}$MOFpath = "$env:SystemDrive\Test\MOF"SCVMMNetworkSettingTest -OutputPath $MOFpath -ConfigurationData $ConfigurationDataStart-DscConfiguration -ComputerName $ComputerName -Wait -Force -Verbose -Debug -Path $MOFpath