param(    [Parameter(Mandatory = $true)]    [System.String]    $ComputerName)$VMMServer = 'TESTVMM01'Configuration SCVMMSwitchExtensionTest{    Import-DscResource -ModuleName xSCVMM    node $ComputerName    {        # Hyper-V VMM Switch Extension configuration examples                xSCVMMSwitchExtension Test_Absent        {            Ensure                          = 'Absent'            VirtualSwitch                   = 'Private'        }                xSCVMMSwitchExtension Test_Present        {            Ensure                          = 'Present'            VirtualSwitch                   = 'External'            VMMLogicalSwitch                = 'VMM_Switch_2'            VMMUplinkPortProfile            = 'VMM_UPP'            VMMServer                       = $VMMServer        }    }} $ConfigurationData = ` @{    AllNodes = `    @(        @{            NodeName = "*"            PSDscAllowPlainTextPassword = $true        }        @{            NodeName = $ComputerName        }    )}$MOFpath = "$env:SystemDrive\Test\MOF"SCVMMSwitchExtensionTest -OutputPath $MOFpath -ConfigurationData $ConfigurationDataStart-DscConfiguration -ComputerName $ComputerName -Wait -Force -Verbose -Debug -Path $MOFpath