param(    [Parameter(Mandatory = $true)]    [System.String]    $ComputerName)Configuration SCVMMUplinkPortProfileTest{    Import-DscResource -ModuleName xSCVMM    node $ComputerName    {        # Uplink Port Profile configuration examples                xSCVMMUplinkPortProfile Test_Remove        {            Ensure                          = 'Absent'            Name                            = 'Test-Remove'        }                xSCVMMUplinkPortProfile Test_UPP        {            Ensure                          = 'Present'            Name                            = 'Test_UPP'            Description                     = 'Test Uplink Port Profile'            LoadBalancingAlgorithm          = "HyperVPort"            TeamMode                        = "SwitchIndependent"            NetworkSite                     = @("Site_0;LN_Management")            NetworkVirtualization           = $true        }                xSCVMMUplinkPortProfile Test_UPP_Defaults        {            Name                            = 'Test_UPP_Defaults'            NetworkSite                     = @("Site_0;LN_Management")        }            }} $ConfigurationData = ` @{    AllNodes = `    @(        @{            NodeName = "*"            PSDscAllowPlainTextPassword = $true        }        @{            NodeName = $ComputerName        }    )}$MOFpath = "$env:SystemDrive\Test\MOF"SCVMMUplinkPortProfileTest -OutputPath $MOFpath -ConfigurationData $ConfigurationDataStart-DscConfiguration -ComputerName $ComputerName -Wait -Force -Verbose -Debug -Path $MOFpath