param(    [Parameter(Mandatory = $true)]    [System.String]    $ComputerName)Configuration SCVMMVMNetworkTest{    Import-DscResource -ModuleName xSCVMM    node $ComputerName    {        # VM Network configuration examples                xSCVMMVMNetwork Management        {            Ensure                          = 'Present'            Name                            = 'Management'            Description                     = 'Management VM Network'            LogicalNetwork                  = 'Management'            IsolationVMNetworkProtocol      = 'IPv4'            IsolationLogicalNetworkProtocol = 'IPv4'            VMSubnets                       = @('Management;10.10.12.0/24-0-0')            AutoCreateSubnet                = $false            NetworkSite                     = ''            StrictConfiguration             = $true        }        xSCVMMVMNetwork Management_NoIsolation        {            Ensure                          = 'Present'            Name                            = 'Management-NoIsolation'            Description                     = 'Management VM Network - No Isolation'            LogicalNetwork                  = 'Management'        }        xSCVMMVMNetwork Test_Remove        {            Ensure                          = 'Absent'            Name                            = 'Test-Remove'            LogicalNetwork                  = 'Management'        }        xSCVMMVMNetwork VLAN_Based        {            Ensure                          = 'Present'            Name                            = 'VLAN-Based-VMNetwork'            LogicalNetwork                  = 'VLAN-Based-Test'            AutoCreateSubnet                = $true        }        xSCVMMVMNetwork PVLAN_Based        {            Ensure                          = 'Present'            Name                            = 'PVLAN-Based-VMNetwork'            LogicalNetwork                  = 'PVLAN-Based-Test'            VMSubnets                       = @('PVLAN_0;10.180.20.0/24-0-1')            NetworkSite                     = 'PVLAN-Based-Test_0'        }    }} $ConfigurationData = ` @{    AllNodes = `    @(        @{            NodeName = "*"            PSDscAllowPlainTextPassword = $true        }        @{            NodeName = $ComputerName        }    )}$MOFpath = "$env:SystemDrive\Test\MOF"SCVMMVMNetworkTest -OutputPath $MOFpath -ConfigurationData $ConfigurationDataStart-DscConfiguration -ComputerName $ComputerName -Wait -Force -Verbose -Debug -Path $MOFpath